import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, {
  Navigation,
  Pagination,
  Autoplay,
} from "swiper/core";
import "swiper/swiper-bundle.css";
import "swiper/swiper.min.css";
import Json from "../portfolio.json";
SwiperCore.use([Navigation, Pagination, Autoplay]);

function importAll(r) {
  return r.keys().map(r);
}
const images = importAll(require.context('../images/sliders', false, /\.(png|jpe?g|svg)$/));
console.log(images);


export default function BottomSwiperPortfolio() {

	const slides = [];
	const url = Json.sites[this.props.thisSiteIndex].url;
	images.forEach(image => {
		const regex = new RegExp('.*?' + url + '_[0-9]+.*', 'gi');
		const num_regex = new RegExp('.*?' + url + '(_[0-9]+).*', 'gi');

		if (regex.test(image)){
			const num = image.match(num_regex);
			console.log(num);
			slides.push(
				<SwiperSlide id="" className="portfolio-bottom-slide">

				</SwiperSlide>
			)
		}
	});
	
	return(
		<Swiper className="portfolio-bottom-slider"
		id="portfolio-bottom-slider"
		slidesPerView={4}
		spaceBetween={25}
		navigation
		>
		{/* {slides} */}
		</Swiper>
	)
}
