import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, {
  Navigation,
  Pagination,
  Autoplay,
  Virtual
} from "swiper/core";
import "swiper/swiper-bundle.css";
import "swiper/swiper.min.css";
import Json from "../slider.json";

// import {main_2} from "../images/skorodumov.net/";






SwiperCore.use([Navigation, Pagination, Autoplay, Virtual]);

export default function MainSwiperPortfolio() {
  const slides = [];

  Json.sites.forEach((site, index) => {
    slides.push(
      <SwiperSlide className="portfolio-slide" key={site.url}>
        <img className="img portfolio-slide__img" src={require(`../images/sliders/${site.url}_01.png}`)} alt={site.url}/>
      </SwiperSlide>
    );
  });
  return (
    <Swiper
      id="swiper"
      slidesPerView={1}
      // slidesPerColumn={2}
      // slidesPerColumnFill="row"
      spaceBetween={30}
      // slidesPerGroup={2}
      // autoplay
      loop
      // onReachEnd={() => {
      //   console.log("reach end");
      //   const tmp = slides.unshift();
      //   slides.push(tmp);
      // }}
      navigation
      pagination={
				{"clickable": true}
			}
    >
      {slides}
    </Swiper>
  );
}
