import React, {useEffect, useState} from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, {
  Navigation,
  Pagination,
  Autoplay,
	Keyboard
} from "swiper/core";
import "swiper/swiper-bundle.css";
import "swiper/swiper.min.css";
import Json from "../portfolio.json";
SwiperCore.use([Navigation, Pagination, Autoplay, Keyboard]);

function importAll(r) {
  return r.keys().map(r);
}
const images = importAll(require.context('../images/sliders', false, /\.(png|jpe?g|svg)$/));




const openFullSlider = (siteIndex, num, url) => {
	console.log(siteIndex, num, url);
}

export default function BottomSwiperPortfolio({thisSiteIndex}) {

	const [swiper, setSwiper] = useState(null);
	useEffect(() => {
		const keydownHandler = e => {
			if (swiper && e.keyCode === 32){
				e.preventDefault();
				swiper.slideNext();
			}
		}
		document.addEventListener('keydown', keydownHandler);

		
		return () => {
			document.removeEventListener('keydown', keydownHandler);
		};

	}, [swiper]);
	

	
	// const swiperRef = useRef(null);

	// const func = () => {
	// 	alert('Yes');
	// }

	
	const slides = [];
	const url = Json.sites[thisSiteIndex].url;
	images.forEach(image => {
		const regex = new RegExp('.*?' + url + '_[0-9]+.*', 'gi');
		const num_regex = new RegExp(url + '(_[0-9]+)', 'gi');

		if (regex.test(image)){
			let num = (image.match(num_regex))[0].match(/[0-9]+/gm)[0];

			console.log(num);
			slides.push(
				<SwiperSlide key={num} id={num} className="portfolio-bottom-slide" onClick={() => {openFullSlider(thisSiteIndex, num, image)}}>
					<img className="portfolio-bottom-image" src={image} alt={'photo' + num}/>
				</SwiperSlide>
			)
		}
	});
	
	return(
		<Swiper className="portfolio-bottom-slider"
		id="portfolio-bottom-slider"
		slidesPerView={4}
		spaceBetween={25}
		navigation
		pagination
		keyboard={{
			enabled: true
		}}
		// onInit={(ev) => {
		// 	set_my_swiper(ev);
		// }}
		onSwiper={setSwiper}
		// ref={swiperRef}
		>
		{slides}
		{/* <button  onClick={() => {func()}}>Click</button> */}
		</Swiper>
	)
}
