import React from "react";
import * as ReactDOMClient from 'react-dom/client';
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, {
  Navigation,
  Pagination,
  Autoplay
} from "swiper/core";
import "swiper/swiper-bundle.css";
import "swiper/swiper.min.css";
import Json from "../portfolio.json";
import BottomSwiperPortfolio from "./BottomSwiperPortfolio";

SwiperCore.use([Navigation, Pagination, Autoplay]);
const allDescs = document.querySelectorAll('.portfolio-descblock-c');
const changePortfolioBlock = (index) => {
  let n = Json.sites[index].url;
  const thisBlock = document.querySelector(`#${n.slice(0, n.indexOf('.'))}`);
  allDescs.forEach(d => {
    d.classList.remove('portfolio-descblock-c_visible')
  });
  thisBlock.classList.add('portfolio-descblock-c_visible');
  
}

allDescs.forEach((desc, index) => {
  const r = ReactDOMClient.createRoot(desc.querySelector('.portfolio-descblock__bottom'));
  r.render();
});
let descblocks = []
Json.sites.forEach((site, index) => {
  descblocks.push(
    <div id={site.url.slice(0, site.url.indexOf('.'))} class="portfolio-descblock-c">
      <div class="portfolio-descblock__top">
        <div class="portfolio-descblock__left">
          <img class="portfolio-descblock__size-image" src={`images/portfolio/${site.url}_sizes.png`} alt={`${site.url}_sizes`}/>
        </div>
        <div class="portfolio-descblock__description">
          <h3 class="portfolio-descblock__title">{site.name}</h3>
          <p class="portfolio-descblock__text">{site.desc}</p>
          <a href={`http://${site.url}`} class="portfolio-descblock__link">{site.url}}</a>
        </div>
      </div>
      <div class="portfolio-descblock__bottom">
        <BottomSwiperPortfolio thisSiteIndex={index}/>
      </div>
    </div>
  );
});
const descblock = ReactDOMClient.createRoot(document.querySelector('.portfolio-descblock'));
descblock.render(<div className="portfolio-descblock__container">
  {descblocks}
</div>);







export default function MainSwiperPortfolio() {
  const slides = [];

  Json.sites.forEach((site, index) => {
    slides.push(
      <SwiperSlide className="portfolio-slide" key={index}>
        <img className="img portfolio-slide__img" src={require(`../images/sliders/${site.url}_01.png`)} alt={site.url}/>
      </SwiperSlide>
    );
  });
  return (
    <Swiper
      id="swiper"
      slidesPerView={1}
      spaceBetween={45}
      loop
      navigation
      pagination={
				{"clickable": true}
			}
      onSlideChange={(sw) => changePortfolioBlock(sw.realIndex)}
    >
      {slides}
    </Swiper>
  );
}
