import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, {
  Navigation,
  Pagination,
  Autoplay,
  Virtual
} from "swiper/core";
import "swiper/swiper-bundle.css";
import "swiper/swiper.min.css";
import Json from "../slider.json";
import main from "../images/skorodumov.net/main.png";
const sites = Json.sites;

function importAll(r) {
  return r.keys().map(r);
}
const images = [];
sites.map(url => {
  console.log(url);
  // let u = '../images/skorodumov.net';
  images.push(importAll(require.context(url, false, /main\.png/)))
});

SwiperCore.use([Navigation, Pagination, Autoplay, Virtual]);



export default function MainSwiperPortfolio() {
  const slides = [];

  sites.map((site, index) => {
    slides.push(
      <SwiperSlide className="portfolio-slide" key={site}>
        <img className="img portfolio-slide__img" src={images[index]} alt={site}/>
      </SwiperSlide>
    );
  });

  // for (let i = 0; i < 12; i++) {
  //   slides.push(
  //     <SwiperSlide key={`slide-${i}`} style={{ listStyle: "none" }}>
  //       <div className="slide">
  //         <h3>{i}</h3>
  //       </div>
  //     </SwiperSlide>
  //   );
  // }

  return (
    <Swiper
      id="swiper"
      slidesPerView={1}
      // slidesPerColumn={2}
      // slidesPerColumnFill="row"
      spaceBetween={30}
      // slidesPerGroup={2}
      // autoplay
      loop
      // onReachEnd={() => {
      //   console.log("reach end");
      //   const tmp = slides.unshift();
      //   slides.push(tmp);
      // }}
      navigation
      pagination={
				{"clickable": true}
			}
    >
      {slides}
    </Swiper>
  );
}
