import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, {
  Navigation,
  Pagination,
  Autoplay,
  Virtual
} from "swiper/core";
import "swiper/swiper-bundle.css";
import "swiper/swiper.min.css";
import Json from "../slider.json";

// import {main_2} from "../images/skorodumov.net/";
const sites = Json.sites;
const mainUrl = Json.main_url;

// function importAll(r) {
//   return r.keys().map(r);
// }
// const images = [];
// sites.map(url => {
//   console.log(url);
//   // let u = '../images/skorodumov.net';
//   images.push(importAll(require.context(`../images/${url}`, false, /\.(png|jpeg|svg)$/)))
// });



SwiperCore.use([Navigation, Pagination, Autoplay, Virtual]);

const fs = require("fs");
const testFolder = '../images';


fs.readdirSync(testFolder).forEach(file => {
  console.log(file);
});



export default function MainSwiperPortfolio() {
  const slides = [];

  sites.map((site, index) => {
    slides.push(
      <SwiperSlide className="portfolio-slide" key={site}>
        <img className="img portfolio-slide__img" src={require(`../images/${site}/${mainUrl}`)} alt={site}/>
      </SwiperSlide>
    );
  });
  return (
    <Swiper
      id="swiper"
      slidesPerView={1}
      // slidesPerColumn={2}
      // slidesPerColumnFill="row"
      spaceBetween={30}
      // slidesPerGroup={2}
      // autoplay
      loop
      // onReachEnd={() => {
      //   console.log("reach end");
      //   const tmp = slides.unshift();
      //   slides.push(tmp);
      // }}
      navigation
      pagination={
				{"clickable": true}
			}
    >
      {slides}
    </Swiper>
  );
}
