import React, {useEffect, useState} from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, {
  Navigation,
  Pagination,
  Autoplay,
	Keyboard
} from "swiper/core";
import "swiper/swiper-bundle.css";
import "swiper/swiper.min.css";
import Json from "../portfolio.json";
let sites = [];
function importAll(r) {
  return r.keys().map(r);
}
const images = importAll(require.context('../images/sliders', false, /\.(png|jpe?g|svg)$/));
const [swiper, setSwiper] = useState(null);
Json.sites.forEach((site, index) =>{
	let url = site.url;
	let siteImages = [];
	images.forEach(image => {
		const regex = new RegExp('.*?' + url + '_[0-9]+.*', 'gi');
		const num_regex = new RegExp(url + '(_[0-9]+)', 'gi');

		if (regex.test(image)){
			let num = (image.match(num_regex))[0].match(/[0-9]+/gm)[0];
			siteImages.push(
				<SwiperSlide key={num} id={num} className="portfolio-fullscreen-slide">
					<img className="portfolio-fullscreen-image" src={image} alt={'photo' + num}/>
				</SwiperSlide>)
	}});
	sites.push(<Swiper className="portfolio-bottom-slider"
	id={`portfolio-full-slider-${index}`}
	slidesPerView={1}
	spaceBetween={50}
	navigation
	pagination
	keyboard={{
		enabled: true
	}}
	onSwiper={setSwiper}
	>
		{siteImages}

	</Swiper>)
});

export default function FullScreenSwiper() {
	
	useEffect(() => {
		const keydownHandler = e => {
			if (swiper && e.keyCode === 32){
				e.preventDefault();
				swiper.slideNext();
			}
		}
		document.addEventListener('keydown', keydownHandler);

		
		return () => {
			document.removeEventListener('keydown', keydownHandler);
		};

	}, [swiper]);

	return (<div className="container portfolio-fullscreen__container">
		{sites}
	</div>)

}
