import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, {
  Navigation,
  Pagination,
  Autoplay,
  Virtual
} from "swiper/core";
import "swiper/swiper-bundle.css";
import "swiper/swiper.min.css";
import Json from "../portfolio.json";

SwiperCore.use([Navigation, Pagination, Autoplay, Virtual]);
let n = Json.sites[0].url;
document.querySelector(`#${n.slice(0, n.indexOf('.'))}`).classList.add('portfolio-descblock-c_visible');
const allDescs = document.querySelectorAll('.portfolio-descblock-c');

const changePortfolioDesc = (index) => {
  let n = Json.sites[index].url;
  allDescs.forEach(d => {
    d.classList.remove('portfolio-descblock-c_visible')
  });
  document.querySelector(`#${n.slice(0, n.indexOf('.'))}`).classList.add('portfolio-descblock-c_visible');
}

export default function MainSwiperPortfolio() {
  const slides = [];

  Json.sites.forEach((site, index) => {
    slides.push(
      <SwiperSlide className="portfolio-slide" key={index}>
        <img className="img portfolio-slide__img" src={require(`../images/sliders/${site.url}_01.png`)} alt={site.url}/>
      </SwiperSlide>
    );
  });
  return (
    <Swiper
      id="swiper"
      slidesPerView={1}
      spaceBetween={45}
      loop
      navigation
      pagination={
				{"clickable": true}
			}
      onSlideChange={(sw) => changePortfolioDesc(sw.realIndex)}
    >
      {slides}
    </Swiper>
  );
}
